INSERT INTO `` (`ID_PDF`,`PDF_DESCRIPCION`,`PDF_CODIGO`) VALUES ('SINIESTRO_AUTO_S','PLANTILLA PARA SINEISTROS AUTOS SURA','<style type=\"text/css\">\n<!--\ndiv.zone\n{\n    border: solid 2mm #66AACC;\n    border-radius: 3mm;\n    padding: 1mm;\n    background-color: #FFEEEE;\n    color: #440000;\n}\ndiv.zone_over\n{\n    width: 30mm;\n    height: 35mm;\n    overflow: hidden;\n}\n\n\ntable.page_header {width: 100%; border: none; background-color: #00AEEA; border-bottom: solid 1mm #0B6E9C; padding: 2mm }\ntable.page_footer {width: 100%; border: none;   padding: 2mm}\nh1 {color: #000033}\nh2 {color: #000055}\nh3 {color: #000077}\n\ndiv.standard\n{\n    padding-left: 5mm;\n}\n\ntable.siniestro {\n   width: 100%;\n   border: 1px solid #000;\n}\n\n\n-->\n</style>\n<page style=\"font-size: 10pt\">\n\n    <page_footer>\n        <table class=\"page_footer\">\n            <tr>\n                <td style=\"width: 100%; text-align: right; \">\n                    pagina [[page_cu]]/[[page_nb]]\n                </td>\n            </tr>\n        </table>\n    </page_footer>\n    \n    <table class=\"page_header\">\n            <tr>\n                <td style=\"width: 100%; text-align: left; font-size: 18pt;font-weight: bold; color:#FFFFFF\">\n                    ALESI\n                </td>\n            </tr>\n        </table>\n    <br>\n    <p style=\"text-align: right; font-weight: bold\">Ciudad de México a ?v_FECHA_PDF?</p>\n    <p style=\"text-align: right; font-weight: bold\">Ref. Int ?v_REF_INT?</p><br>\n    <br>\n    <b>Seguros Sura S.A. de C.V </b><br>\n    <b>?DIRIGIDO_A?</b><br>\n    <b>?CARGO?</b><br>\n    <b>Presente.</b><br>\n    <p style=\"text-align: right; font-weight: bold\">Asunto: Informe de Investigación de Siniestro</p><br>\n    <p style=\"text-align: justify\"> \n        &nbsp;&nbsp;&nbsp;&nbsp; Estimado ?DIRIGIDO_A?: En relación al siniestro citado anteriormente, \n        mismo que nos encomendó hacer un análisis, e investigación del hecho ocurrido, y de acuerdo a las diferentes técnicas y métodos de investigación \n        empleados le proporciono el siguiente informe detallado\n    </p>\n    <p style=\"text-align: center; font-weight: bold\">DATOS DEL SINIESTRO</p><br>\n    <table >\n        <tbody >\n            <tr>\n                <th>No. de Siniestro</th>\n                <th></th>\n                <th>Fecha del Siniestro</th>\n                <th></th>\n                <th>Fecha del Reporte</th>\n                \n            </tr>\n            <tr >\n                <td>?N_DE_SINIESTRO?</td>\n                <th></th>\n                <td>?F_DE_SINIESTRO?</td>\n                <th></th>\n                <td>?F_DE_REPORTE?</td>            \n            </tr>\n        </tbody>\n            <tr style=\"vertical-align: top\">\n                <td>No. de Póliza</td>\n                <td>Inciso</td>\n                <td>Inicio Vigencia</td>\n                <td>Término Vigencia</td>\n                <td>Coberturas</td>\n            </tr>       \n            <tr style=\"vertical-align: top\">\n                <td>?N_DE_POLIZA?</td>\n                <td>?INCISO?</td>\n                <td>?INI_VIG?</td>\n                <td>?FIN_VIG?</td>\n                <td>?COBERTURA?</td>\n            </tr>\n            <tr style=\"vertical-align: top\">\n                <td>Nombre del Asegurado: </td>\n                <th>?NOM_ASEGURADO?.</th>\n            </tr>\n            <tr style=\"vertical-align: top\">\n                <td>Nombre del Conductor: </td>\n                <th>?NOM_CONDUCTOR?</th>\n            </tr>\n            <tr style=\"vertical-align: top\">\n                <td>Tipo de siniestro Robo: </td>\n				<td>?CON_VIOLENCIA?</td>\n                <td>?SIN_VIOLENCIA?</td>\n                <td>?EN_DOMICILIO?</td>\n                <td>?COLISION_VUELCO?</td>\n            </tr>\n    </table>\n    <br>\n    <p style=\"text-align: center; font-weight: bold\">DATOS DEL VEHÍCULO.</p><br>\n    <table style=\"text-align: cen